import g9 from "g9"

var data = {
    "r": 100,
    "theta": 0
}
var folds = 4;

function render(data, ctx){
    for(var k = 0; k < 2 * Math.PI; k += Math.PI / folds){
    	ctx.circle(Math.cos(data.theta + k) * data.r, Math.sin(data.theta + k) * data.r) 
    }
}

function svg_circle(x, y){
    var circle = document.createElementNS("http://www.w3.org/2000/svg", "circle");
    circle.setAttribute("cx", x);
    circle.setAttribute("cy", y);
    circle.setAttribute("r", 5);
    circle.setAttribute("pointer-events", "none");
    return circle
}

function change(data){
    for(var k = 0; k < 2 * Math.PI; k += Math.PI / folds){
        instance.node.appendChild(svg_circle(Math.cos(data.theta + k) * data.r, Math.sin(data.theta + k) * data.r))
    }
}

var instance = g9(data, render, change)
instance.insertInto('#root')

