import g9 from "g9"

var data = {
    r: 100,
    theta: 0
}

function render(data, ctx){
    var r = data.r,
        theta = data.theta;
    var points = 6
    for(var i = 0; i < 2 * points; i++){
        var new_theta = theta + Math.PI * (1 + 1/points)
        ctx.line(r * Math.cos(theta), r * Math.sin(theta),
                r * Math.cos(new_theta), r * Math.sin(new_theta), {
            affects: ['r', 'theta'],
            'stroke-width': 5
        });
        theta = new_theta   
    }
}

g9(data, render)
    .insertInto('#root')