import g9 from "g9"

var data = {
    "fahrenheit": 72
}

function render(data, ctx){
    ctx.rect(0, 0, 20, -data.fahrenheit, {
        fill: 'red'
    })
    
    var celsius = 5/9 * (data.fahrenheit - 32)
    ctx.rect(-100, 0, 20, -celsius, {
        fill: 'red'
    })
    
    ctx.rect(-100, 0, 20, -100, {
        fill: 'none',
        stroke: 'black',
        rx: 10
    })
    
    ctx.rect(0, 0, 20, -100, {
        fill: 'none',
        stroke: 'black',
        rx: 10
    })
    
    ctx.text(celsius.toFixed(1), -100, 20)
    ctx.text(data.fahrenheit.toFixed(1), 0, 20)
    
    // draw the difference
    ctx.rect(-45, -data.fahrenheit, 10, data.fahrenheit - celsius, {
        fill: 'blue'
    })
}

g9(data, render)
    .insertInto('#root')